import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import style from './style.module.scss';
import Option from '../shared/partials/option';
import data from '../../data.json';
import { selectSize, selectTopping } from '../../store/actions/ordered';

const Home = () => {
  const size = useSelector((state) => state.orderReducer.size);
  const toppings = useSelector((state) => state.orderReducer.listTopping);
  const orderValue = useSelector((state) => state.orderReducer.orderValue);

  const dispatch = useDispatch();
  const history = useHistory();

  const handleSelectSizePizza = (size) => {
    dispatch(selectSize(size));
  };

  const handleSelectToppings = (topping) => {
    dispatch(selectTopping(topping));
  };

  // const handleCheckActive = (topping) => {
  //   return toppings.some(item => item.value === topping.value);
  // }

  const continueOrder = () => {
    if (toppings.length > 0 && !!size ) {
      alert('ok');
      history.push('/payment');
    } else {
      alert('please chọn đồ cho đủ');
    }
  };

  // useEffect(() => {
  //   dispatch(saveInfoToppings(toppings))
  // }, [toppings])

  // useEffect(() => {
  //   const priceTopping = toppings.reduce(
  //     (previousValue, currentValue) => previousValue + currentValue.price,
  //     0
  //   );
  //   setTotalPrice(pizzaSelected.price + priceTopping)
  // }, [toppings, pizzaSelected])

  const [listSize, setListSize] = useState([]);
  const [listTopping, setListTopping] = useState([]);

  useEffect(() => {
    setListSize(data.size);
    setListTopping(data.topping);
  }, []);

  return (
    <div className={style.homePage}>
      <img src='./images/bg.jpeg' className='image' alt='bg' />
      <div className={style.orderContent}>
        <h2 className={style.orderTitle}>place your order</h2>
        <hr></hr>
        <ul className={style.listOptionPizza}>
          {listSize.map((item) => (
            <Option
              key={item.id}
              option={item}
              onChange={handleSelectSizePizza}
              selected={size === item}
            />
          ))}
        </ul>
        <ul className={style.listTopping}>
          {listTopping.map((item) => (
            <Option
              key={item.id}
              option={item}
              onChange={handleSelectToppings}
              selected={toppings?.includes(item)}
            />
          ))}
        </ul>
        <div className={style.cartInfo}>
          <span className={style.totalPrice}>{orderValue}</span>
          <button className={style.buttonOrder} onClick={continueOrder}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
