const initialState = {
  selected: [],
  orderValue: 0,
}
const orderReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SELECT_SIZE': {
      const sizeValue = action.payload.price;
      const toppingValue = state.toppingList.reduce((value, element) => value + element.price, 0);

      return {
        ...state,
        size: action.payload,
        orderValue: sizeValue + toppingValue,
      }
    }
    case 'SELECT_TOPPING': {
      const newList = [...state.toppingList];
      const index = newList.findIndex(item => item.id === action.payload.id);
      if (index < 0) {
        newList.push(action.payload)
      } else {
        newList.splice(index, 1);
      }

      const sizeValue = state.size.price;
      const toppingValue = newList.reduce((value, element) => value + element.price, 0);

      return {
        ...state,
        toppingList: newList,
        orderValue: sizeValue + toppingValue
      }
    }
    default:
      return state;
  }
};
export default orderReducer;
