import React, { useEffect } from 'react';
import style from './style.module.scss';
import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import * as CardValidator from 'card-validator';
import { useHistory } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearState } from '../../store/actions/ordered';

const schema = yup
  .object({
    name: yup.string().required('Name is required'),
    phoneNumber: yup
      .string()
      .required('Phone number is required')
      .matches(
        /^(\+\d{1,2}\s?)?1?\-?\.?\s?\(?\d{3}\)?[\s.-]?\d{3}[\s.-]?\d{4}$/,
        'Is not in correct format'
      ),
    address: yup.string().required('Address is required'),
    cardNumber: yup
      .string()
      .required('card number is required')
      .test(
        'errorCode',
        'error card number',
        (val) => CardValidator.number(val).isValid === true
      ),
    expireDate: yup
      .string()
      .required('expire date is required')
      .test(
        'errorExpireDate',
        'error expire date',
        (val) => CardValidator.expirationDate(val).isValid === true
      ),
    securityCode: yup
      .string()
      .required('security code is required')
      .test(
        'errorSecurityCode',
        'error security code',
        (val) => CardValidator.cvv(val,4).isValid === true
      ),
  })
  .required();

const Payment = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    mode: 'onBlur',
    resolver: yupResolver(schema),
  });

  const dispatch = useDispatch();
  const history = useHistory();

  const size = useSelector((state) => state.orderReducer.size);
  const toppings = useSelector((state) => state.orderReducer.listTopping);

  useEffect(() => {
    if (toppings.length === 0 || Object.keys(size).length === 0) {
      directToHomepage();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const directToHomepage = () => {
    history.push('/');
  };

  const onSubmit = (data) => {
    const info = {
      ...data,
      size: size.id,
      toppings: toppings.map((item) => item.id),
    };
    dispatch(clearState());
    alert('Order successful !');
    console.log(JSON.stringify(info));
    directToHomepage();
  };

  return (
    <div className={style.formInfo}>
      <img
        src='./images/banner.png'
        className={style.image}
        alt='banner-pizza'
      />
      <form className={style.form} onSubmit={handleSubmit(onSubmit)}>
        <h2 className={style.formTitle}>Order info</h2>
        <div className={style.formDouble}>
          <label>
            <span className={style.labelText}>Name :</span>
            <input
              className={style.formInput}
              {...register('name', { required: true })}
            />
            {errors.name && (
              <span className={style.error}>{errors.name.message}</span>
            )}
          </label>
          <label>
            <span className={style.labelText}>Phone number :</span>
            <input
              className={style.formInput}
              {...register('phoneNumber', { required: true })}
            />
            {errors.phoneNumber && (
              <span className={style.error}>{errors.phoneNumber.message}</span>
            )}
          </label>
        </div>
        <label>
          <span className={style.labelText}>Address :</span>
          <input
            className={style.formInput}
            {...register('address', { required: true })}
          />
          {errors.address && (
            <span className={style.error}>{errors.address.message}</span>
          )}
        </label>
        <label>
          <span className={style.labelText}>Credit card :</span>
          <input
            className={style.formInput}
            {...register('cardNumber', { required: true })}
          />
          {errors.cardNumber && (
            <span className={style.error}>{errors.cardNumber.message}</span>
          )}
        </label>
        {/* <div className={style.formDouble}>

        </div> */}
        <div className={style.formDouble}>
          <label>
            <span className={style.labelText}>Expiration date :</span>
            <input
              className={style.formInput}
              {...register('expireDate', { required: true })}
            />
            {errors.expireDate && (
              <span className={style.error}>{errors.expireDate.message}</span>
            )}
          </label>
          <label>
            <span className={style.labelText}>Security Code :</span>
            <input
              className={style.formInput}
              {...register('securityCode', { required: true })}
            />
            {errors.securityCode && (
              <span className={style.error}>{errors.securityCode.message}</span>
            )}
          </label>
        </div>
        <div className={style.formDouble}>
          <button
            type='button'
            className={style.btnPrimary}
            onClick={directToHomepage}
          >
            back
          </button>
          <button className={style.btnPrimary}>submit</button>
        </div>
      </form>
      <div className={style.order}></div>
    </div>
  );
};

export default Payment;
