import React, { useEffect, useState } from 'react';
import './style.module.scss';

const randomName = (Math.random() + 1).toString(36).substring(7);

const Checkbox = ({ options, value, onChange, name = randomName, ...rest }) => {
  const [isChecked, setIsChecked] = useState();

  const toggleCheck = () => {
    if (onChange) {
      onChange(!isChecked);
    }
  };

  useEffect(() => {
    console.log(options);
    setIsChecked(value);
  }, [value]);

  return (
    <div>
      {options &&
        options.map((option) => (
          <label className='inputCheckboxLabel' htmlFor={option.id} key={option.id}>
            <input
              checked={isChecked}
              hidden
              type={'checkbox'}
              name={name}
              id={option.id}
              onChange={() => toggleCheck()}
              {...rest}
            />
            <div className='inputCheckbox' />
            <span className='text-white'>{option.text}</span>
          </label>
        ))}
    </div>
  );
};

export default Checkbox;
