// import { types } from "../types"

export const selectSize = (payload) => {
  return {
    type: 'SELECT_SIZE',
    payload
  }
}

export const selectTopping = (payload) => {
  return {
    type: 'SELECT_TOPPING',
    payload
  }
}

export const clearState = () => {
  return {
    type: 'CLEAR_STATE',
  }
}
