import React, { useEffect, useState } from 'react';
import './style.module.scss'

const randomName = (Math.random() + 1).toString(36).substring(7);

const Radio = ({
  options,
  onChange,
  onBlur,
  name = randomName,
  ...rest
}) => {
  const [currentChoice, setCurrentChoice] = useState(null);

  const onChangeRadio = (val) => {
    setCurrentChoice(val);
    onChange && onChange(val);
    onBlur && onBlur(val);
  };

  useEffect(() => {
    if (options.id !== currentChoice) {
      setCurrentChoice(options.id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [options.id]);

  return (
    <div className='inputRadio'>
      {options &&
        options.map((option) => (
          <label
            className='inputRadioLabel'
            key={option.id}
            htmlFor={`radio-custom-${option.id}`}
          >
            <input
              checked={currentChoice === option.id}
              hidden
              type='radio'
              name={name}
              id={`radio-custom-${option.id}`}
              {...rest}
              value={option.id}
              onChange={() => onChangeRadio(option)}
            />
            <div className='inputRadioCheckBox' />
            <span>{option.text}</span>
          </label>
        ))}
    </div>
  );
};

export default Radio;
